css:
/*cabe√ßalho*/
#header .d-lg-block a{
 border-right-style:solid;
 border-right-width:1px;
}

/* Header  buttons */
#header .col-auto .header__buttons{
 border-style:solid;
 border-width:1px;
 transform:translatex(0px) translatey(0px);
 border-color:#baaaaa;
}

 /* Header  buttons */
 #header .col-auto .header__buttons{
  border-top-style:none;
  border-right-style:none;
  border-bottom-style:none;
  border-left-style:none;
 }

 #user-button{
 visibility:hidden;
}

/* Button */
#cart-button{
 visibility:hidden;
}
}

/* Search input */
#search-input{
 border-style:solid;
 border-color:#baaaaa !important;
 border-width:1px;
}

/* menu lateral hover */
.menu__item:hover {
    text-decoration: none;
    background: #05085d;
    color: #ffffff;}
.top-bar {background: #05085d !important;}
.top-bar__nav {color: #fff;}
.top-bar__page-links a {text-decoration: underline;}

/*tarja marcas*/
.brands-carousel .glide .glide__track{
 background-color:#eaeaea;
 text-align:center;
}
.brands-carousel__list a h4{
 background-color:rgba(236,240,241,0);
 color:#868e95;
}

/* Font Icon */
#user-button i{
 border-right-width:1px;
}

/* Button */
#user-button{
 border-right-color:#baaaaa;
 transform:translatex(0px) translatey(0px);
}

/* Product card */
.sections .glide .product-card{
 border-color:#cbccd1;
 border-width:2px;
 border-style:dotted;
 border-top-left-radius:15px;
 border-top-right-radius:1px;
 border-bottom-left-radius:1px;
 border-bottom-right-radius:15px;
}

.product__buy  {display: none;}
.shipping-calculator {display: none;}
.product-card__buy {display: none;}
.info-bar {background-color: #05085d !important;}

@media (max-width:767px){

 /* Header  buttons */
 #header .col-auto .header__buttons{
  border-top-style:none;
  border-right-style:none;
  border-bottom-style:none;
  border-left-style:none;
 }

 /* Button */
 #user-button{
  border-right-style:none;
 }

 /* Button */
 .brands-carousel .glide .btn{
  background-color:#34495e;
  border-top-left-radius:10px;
  border-top-right-radius:10px;
  border-bottom-left-radius:10px;
  border-bottom-right-radius:10px;
 }

 /* Font Icon */
 .brands-carousel .btn .fas{
  color:#ecf0f1;
 }
 }
